// Simple error handler
return {
  json: {
    error: true,
    message: 'Validation failed: Missing required prompt, BGM, or CTA audio fields',
    timestamp: new Date().toISOString()
  }
};

// Prepare data for video generation API
const inputData = $input.all();
console.log('=== MERGE PROCESSOR ===');

// Initialize result object
let result = {
  json: {},
  binary: {}
};

// Process each input item
inputData.forEach((item, index) => {
  console.log(`Processing item ${index}:`, Object.keys(item.json || {}), Object.keys(item.binary || {}));

  // Merge JSON data
  if (item.json) {
    result.json = { ...result.json, ...item.json };
  }

  // Merge binary data
  if (item.binary) {
    result.binary = { ...result.binary, ...item.binary };
  }
});

// Log final structure
console.log('Final JSON keys:', Object.keys(result.json));
console.log('Final binary keys:', Object.keys(result.binary));

// Ensure we have all required fields
const requiredBinaryFields = ['Audio_Bgm', 'Audio_cta', 'audio_file'];
const missingFields = requiredBinaryFields.filter(field => !result.binary[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing binary fields: ${missingFields.join(', ')}`);
}

return result;

// Access input from Manual Trigger or previous node
const input = $input.item.json;

// Optional: fallback/default values
const defaults = {
  prompt: 'faith and hope inspiration',
  cta_url: '',
  bgm_url: ''
};

// Merge the input with defaults
const processedData = {
  prompt: input.prompt || defaults.prompt,
  cta_url: input.cta_url || defaults.cta_url,
  bgm_url: input.bgm_url || defaults.bgm_url,
  timestamp: new Date().toISOString()
};

// Debugging
console.log('=== DATA PROCESSOR ===');
console.log('Prompt:', processedData.prompt);
console.log('CTA URL:', processedData.cta_url);
console.log('BGM URL:', processedData.bgm_url);

// Return the processed result
return {
  json: processedData
};

// Parse AI script output
const inputData = $input.first().json;
console.log('=== SCRIPT PARSER START ===');

let scriptOutput;
if (inputData.output) {
  scriptOutput = typeof inputData.output === 'string' ? JSON.parse(inputData.output) : inputData.output;
} else {
  scriptOutput = inputData;
}

if (!scriptOutput.scripts || !scriptOutput.scripts[0]) {
  throw new Error('No scripts found in AI output');
}

const script = scriptOutput.scripts[0];
const scriptText = script.voice_script;

if (!scriptText || scriptText.length < 10) {
  throw new Error('Script text too short or missing');
}

const result = {
  success: true,
  selected_script: script,
  script_text: scriptText,
  processing_info: {
    script_length: scriptText.length,
    word_count: scriptText.split(' ').length,
    timestamp: new Date().toISOString()
  }
};

console.log('âœ… Script parsed successfully');
console.log('Title:', script.title);
console.log('Length:', scriptText.length);

return {
  json: result
};

